import React, { useState, useEffect } from "react";
import axios from "axios";
import MoodForm from "./MoodForm";
import { Link } from "react-router-dom";
import './HomePage.css';
import Navbar from "./navbar";
import { jwtDecode } from 'jwt-decode';

const Dashboard = () => {
    const [moods, setMoods] = useState([]);
    const [loading, setLoading] = useState(true); // Optional: For loading UI

    // Fetch moods from the backend
    const fetchMoods = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;

        try {
            const decodedToken = jwtDecode(token);
            const userId = decodedToken.id;
            const response = await axios.get(`http://localhost:8080/${userId}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setMoods(response.data);
        } catch (error) {
            console.error("Error fetching moods:", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchMoods();
    }, []);

    // Add a new mood
    const addMood = async (mood) => {
        const token = localStorage.getItem('token');
        if (!token) return;

        const decodedToken = jwtDecode(token); 
        const userId = decodedToken.id;

        try {
            const response = await axios.post("http://localhost:8080/", {
                user_id: userId,
                mood: mood.mood,
                message: mood.message
            }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setMoods([...moods, response.data]);
            fetchMoods();
        } catch (error) {
            console.error("Error adding mood:", error);
            if (error.response?.data?.error) {
                alert(error.response.data.error);
            } else {
                alert('An error occurred: ' + error.message);
            }
        }
    };

    const latestMood = moods.length > 0 ? moods[0] : null;

    return (
        <div id="bg">
            <Navbar />
            <MoodForm onAddMood={addMood} />
            <div className="result-container">
                <h2>Current Mood</h2>
                {loading ? (
                    <p>Loading...</p>
                ) : latestMood ? (
                    <ul className="mood">
                        <li key={latestMood._id}>
                            <h3>Mood: {latestMood.mood}</h3>
                            <p>Message: {latestMood.message}</p>
                            <p>Date: {new Date(latestMood.date).toLocaleString()}</p>
                            <h4>Suggestions:</h4>
                            <ul className="suggestions">
                                {Array.isArray(latestMood.suggestions) && latestMood.suggestions.length > 0 ? (
                                    latestMood.suggestions.map((suggestion, index) => (
                                        <li key={index}>{suggestion}</li>
                                    ))
                                ) : (
                                    <li>No suggestions available</li>
                                )}
                            </ul>
                        </li>
                    </ul>
                ) : (
                    <p>No mood to display</p>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
